/* Javascript functions for plugin: MimeTypes Link Icons */
jQuery(document).ready(function(){var fieldSetImages=jQuery('#images');var checkboxes=fieldSetImages.find(':checkbox');fieldSetImages.before(i18n_mtli.togglebox).after(i18n_mtli.togglebox);jQuery('.check-all').click(function(event){event.preventDefault();checkboxes.attr({'checked':'checked'});return false});jQuery('.uncheck-all').click(function(event){event.preventDefault();checkboxes.removeAttr('checked');return false})});