/* Javascript functions for plugin: MimeTypes Link Icons */
(function(){if(i18n_mtli.enable_async==1){var contentObj=jQuery('#content');if(contentObj.length==0){contentObj=jQuery('.status-publish:first').parent()}var contentLinks=contentObj.find('a');if(i18n_mtli.oldwp==1){i18n_mtli.mime_array=i18n_mtli.mime_array.split(',')}jQuery.each(i18n_mtli.mime_array,function(i,elm){var reg=new RegExp('^([^"#]+\\.'+elm+')(#[^" ]+$|$)','i');contentLinks.filter(function(){return(jQuery(this).attr('href').search(reg)>=0)}).addClass('mtli_attachment mtli_'+elm)})}if(i18n_mtli.hidethings==1){jQuery(i18n_mtli.avoid_selector).each(function(){if(jQuery(this).hasClass('mtli_attachment')){jQuery(this).removeClass('mtli_attachment').css('background-image','none');if(jQuery(this).attr('rel')!=undefined&&jQuery(this).attr('rel').indexOf('mtli_filesize')>=0){jQuery(this).removeAttr('rel')}}jQuery(this).find('.mtli_attachment').removeClass('mtli_attachment').css('background-image','none');jQuery(this).find('[rel^="mtli_filesize"]').removeAttr('rel')})}})(jQuery);